package communityuni.com;

public class TestKieuDuLieu {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("Min của int là: " +Integer.MIN_VALUE);
		System.out.println("Max của int là: " +Integer.MAX_VALUE);
		System.out.println("Max của double là: " +Double.MAX_VALUE);
		System.out.println("Min của double là: " +Double.MIN_VALUE);
		System.out.println("Min của float là: "+Float.MIN_VALUE);
		System.out.println("Max của float là: "+Float.MAX_VALUE);
		boolean b = true;
		float f = 1.5f;
		double a = 1.5;
		System.out.println(a);
		System.out.println(f);
		System.out.println(b);
		
		
	}

}
